import request from 'superagent';

function loadFailure(){
  return {type: "LOAD_FAILURE"}
}

function loadSuccess(items){
  return {type: "LOAD_SUCCESS", items}
}

export function loadMovie(url){
  return dispatch => {
    return request
    .get(url)
    .set('Accept', 'application/json')
    .end((err, res) => {
      if(err){
        console.error(err)
        dispatch(loadFailure())
      }else{
        dispatch(loadSuccess(res.body.results))
      }
    })
  }
}

function loadDetailFailure(){
  return {type: "LOAD_DETAIL_FAILURE"}
}

function loadDetailSuccess(details){
  return {type: "LOAD_DETAIL_SUCCESS", details}
}

export function loadDetailMovie(url){
  return dispatch => {
    return request
    .get(url)
    .set('Accept', 'application/json')
    .end((err, res) => {
      if(err){
        console.error(err)
        dispatch(loadFailure())
      }else{
        dispatch(loadSuccess(res.body))
      }
    })
  }
}
