import React, { Component } from 'react'
import TextTruncate from 'react-text-truncate';
import CircularProgressbar from 'react-circular-progressbar';
import { NavLink } from 'react-router-dom'
import 'react-circular-progressbar/dist/styles.css';

export default class MovieCard extends Component {

  // componentDidMount(){
  //   this.props.actions.getMovie(this.props.movie.id);
  // }

  render(){
    const {movie, getMovie} = this.props
    let purchased = []
    if (this.props.purchasedlist.length !== 0) {
      purchased = JSON.parse(this.props.purchasedlist)
      purchased = purchased.purchasedlist
    }
    let ispurchased;
    if(purchased.indexOf(movie.id) < 0){
      ispurchased = '';
    }else{
      ispurchased = '<div>Purchased</div>';
    }

    return(
      <div>
      <div className="ispurchased" dangerouslySetInnerHTML={{__html: ispurchased}}></div>
      <div className="movie-card">

        <div className="movie-header myBackgroundImg" style={{background: `url(https://image.tmdb.org/t/p/w500${movie.poster_path})`}}>
          <div className="header-icon-container">
            <NavLink to={`/${movie.id}-${movie.original_title.replace(/ /g, '-')}`} onClick={()=>{getMovie(movie.id)}}>
              <i className="material-icons header-icon"></i>
            </NavLink>
          </div>
        </div>

        <div className="movie-content manOfSteel">
          <div className="movie-content-header">
            <NavLink to={`/${movie.id}-${movie.original_title.replace(/ /g, '-')}`} onClick={()=>{getMovie(movie.id)}}>
              <h3 className="movie-title">{movie.title}</h3>
            </NavLink>
            <div className="imax-logo">
              <div style={{ width: '50px' }}>
                <CircularProgressbar
                 percentage={movie.vote_average * 10} text={movie.vote_average}
                 />
              </div>
            </div>
          </div>
          <br />
          <TextTruncate
          line={3}
          truncateText="…"
          text={movie.overview}
          />

          <div className="movie-info">
            <div className="info-section">
              <label>Release Date</label>
              <span>{movie.release_date}</span>
            </div>
            <div className="info-section">
              <label>Popularity</label>
              <span>{movie.popularity}</span>
            </div>
            <div className="info-section">
              <label>Vote Count</label>
              <span>{movie.vote_count}</span>
            </div>
          </div>
        </div>
      </div>
      </div>
    )
  }
}
