import React, { Component } from 'react'
import {bindActionCreators} from 'redux'
import {connect} from 'react-redux'
import * as AppActions from '../../actions'

class Trailers extends Component {

  componentDidMount(){
    this.props.actions.getTrailers(this.props.movieID);
  }

  render(){
    const {trailers, actions} = this.props

    console.log(this.props);
    return(
      <div>
      {trailers.map((item) => {
        let profile_img = `https://image.tmdb.org/t/p/w185${item.profil_path}`;
        if(item.profile_path === 'null' || item.profile_path === '' || typeof item.profile_path !== 'string'){
          profile_img = 'https://www.brikatsuper.com/storages/2018/04/no_photo.jpg';
        }
        return(
          <div className="movie-card_content" key={item.cast_id}>
          <img style={{height: 180, width: 150}} src={profile_img} alt={item.name} className="movie-card__image" />
            <div className="movie-card__content">
            <h3 className="movie-card-content__title">{item.name}</h3>
            <p className="movie-card-content__text">{item.character}</p>
            </div>
          </div>
        )
      })}
      </div>
    )
  }
}

function mapStateToProps(state){
  return{
    trailers: state.trailers
  }
}

function mapDispatchToProps(dispatch){
  return{
    actions: bindActionCreators(AppActions, dispatch)
  }
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Trailers)
