import React, { Component } from 'react'
import {bindActionCreators} from 'redux'
import {connect} from 'react-redux'
import * as AppActions from '../../actions'

class Casts extends Component {

  componentDidMount(){
    this.props.actions.getCasts(this.props.movieID);
  }

  render(){
    const {casts, actions} = this.props
    return(
      <div className="movie-card_content" key={casts.id}>
      <img style={{height: 180, width: 150}} src={`https://image.tmdb.org/t/p/w185${casts.profil_path}`} alt={casts.name} className="movie-card__image" />
        <div className="movie-card__content">
        <h3 className="movie-card-content__title">{casts.name}</h3>
        <p className="movie-card-content__text">{casts.character}</p>
        </div>
      </div>
    )
  }
}

function mapStateToProps(state){
  return{
    casts: state.casts
  }
}

function mapDispatchToProps(dispatch){
  return{
    actions: bindActionCreators(AppActions, dispatch)
  }
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Casts)
