import React, { Component } from 'react'
import {bindActionCreators} from 'redux'
import {connect} from 'react-redux'
import * as AppActions from '../../actions'

class Casts extends Component {

  componentDidMount(){
    this.props.actions.getCasts(this.props.movieID);
  }

  render(){
    const {casts, actions} = this.props
    let cast = casts.map((item) => {
      return(
        <div className="movie-card_content">
        <img style={{height: 180, width: 150}} src={`https://image.tmdb.org/t/p/w185${item.profil_path}`} alt={item.name} className="movie-card__image" />
          <div className="movie-card__content">
          <h3 className="movie-card-content__title">{item.name}</h3>
          <p className="movie-card-content__text">{item.character}</p>
          </div>
        </div>
      )
    })
    return(
      {cast}
    )
  }
}

function mapStateToProps(state){
  return{
    casts: state.casts
  }
}

function mapDispatchToProps(dispatch){
  return{
    actions: bindActionCreators(AppActions, dispatch)
  }
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Casts)
