import React, { Component } from 'react'
import {bindActionCreators} from 'redux'
import {connect} from 'react-redux'
import * as AppActions from '../../actions'

class Casts extends Component {

  componentDidMount(){
    const movieID = this.props.movieID;
    this.props.actions.getCasts(movieID);
  }

  render(){
    const {casts, actions} = this.props

    console.log(this.props);
    let profile_img = `https://image.tmdb.org/t/p/w185${casts.cast.profile_path}`;
    if(casts.cast.profile_path === 'null' || casts.cast.profile_path === '' || typeof casts.cast.profile_path !== 'string'){
      profile_img = 'https://www.brikatsuper.com/storages/2018/04/no_photo.jpg';
    }
    return(
      <div className="movie-card_content">
      <img style={{height: 180, width: 150}} src={profile_img} alt={casts.cast.name} className="movie-card__image" />
        <div className="movie-card__content">
        <h3 className="movie-card-content__title">{casts.cast.name}</h3>
        <p className="movie-card-content__text">{casts.cast.character}</p>
        </div>
      </div>
    )
  }
}

function mapStateToProps(state){
  return{
    casts: state.casts
  }
}

function mapDispatchToProps(dispatch){
  return{
    actions: bindActionCreators(AppActions, dispatch)
  }
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Casts)
